option.value:
# Script de prueba para verificar la conexión con el backend

Write-Host "================================" -ForegroundColor Cyan
Write-Host "DIAGNÓSTICO DE LOGIN" -ForegroundColor Cyan
Write-Host "================================" -ForegroundColor Cyan
Write-Host ""

# Test 1: Verificar que el backend está corriendo
Write-Host "Test 1: Verificando backend..." -ForegroundColor Yellow
try {
    $health = Invoke-RestMethod -Uri "http://localhost:5001/api/health" -Method Get
    Write-Host "✓ Backend está corriendo: $($health.status)" -ForegroundColor Green
} catch {
    Write-Host "✗ Backend no responde: $($_.Exception.Message)" -ForegroundColor Red
    exit 1
}

Write-Host ""

# Test 2: Intentar registrar un usuario de prueba
Write-Host "Test 2: Registrando usuario de prueba..." -ForegroundColor Yellow
$testUser = @{
    email = "test@test.com"
    password = "test123"
    firstName = "Test"
    lastName = "User"
} | ConvertTo-Json

try {
    $register = Invoke-RestMethod -Uri "http://localhost:5001/api/auth/register" `
        -Method Post `
        -Body $testUser `
        -ContentType "application/json"
    Write-Host "✓ Usuario registrado exitosamente" -ForegroundColor Green
    Write-Host "Token: $($register.token.Substring(0, 20))..." -ForegroundColor Gray
} catch {
    if ($_.Exception.Message -like "*User already exists*" -or $_.ErrorDetails.Message -like "*User already exists*") {
        Write-Host "⚠ Usuario ya existe (esto es OK)" -ForegroundColor Yellow
    } else {
        Write-Host "✗ Error al registrar: $($_.Exception.Message)" -ForegroundColor Red
        Write-Host "Detalles: $($_.ErrorDetails.Message)" -ForegroundColor Red
    }
}

Write-Host ""

# Test 3: Intentar hacer login
Write-Host "Test 3: Intentando login..." -ForegroundColor Yellow
$loginData = @{
    email = "test@test.com"
    password = "test123"
} | ConvertTo-Json

try {
    $login = Invoke-RestMethod -Uri "http://localhost:5001/api/auth/login" `
        -Method Post `
        -Body $loginData `
        -ContentType "application/json"
    Write-Host "✓ Login exitoso" -ForegroundColor Green
    Write-Host "Usuario: $($login.user.firstName) $($login.user.lastName)" -ForegroundColor Gray
    Write-Host "Email: $($login.user.email)" -ForegroundColor Gray
    Write-Host "Token: $($login.token.Substring(0, 20))..." -ForegroundColor Gray
} catch {
    Write-Host "✗ Error al hacer login: $($_.Exception.Message)" -ForegroundColor Red
    Write-Host "Detalles: $($_.ErrorDetails.Message)" -ForegroundColor Red
    Write-Host ""
    Write-Host "Respuesta del servidor:" -ForegroundColor Yellow
    Write-Host $_.ErrorDetails.Message -ForegroundColor Red
}

Write-Host ""
Write-Host "================================" -ForegroundColor Cyan
Write-Host "FIN DEL DIAGNÓSTICO" -ForegroundColor Cyan
Write-Host "================================" -ForegroundColor Cyan

